const Interface = require('./Interface');

class CancelBuild extends Interface {
  constructor({
    urlPath = '/NDP/cancelBuild',
    urlName = '取消构建',
    env,
  } = {}) {
    super({ urlPath, urlName, env });
  }

  cancel(clusterId, workflowId) {
    if (!Number.isInteger(clusterId) || !Number.isInteger(workflowId)) {
      throw new Error(`${this.urlName}需要clusterId和workflowId`);
    }
    return this.sendRequest({
      urlArgs: { clusterId, workflowId },
    });
  }
}

module.exports = CancelBuild;
