const chalk = require('chalk');
const semver = require('semver');

const checkNodeAndNpmVersion = () => {
  const nodeVersion = process.versions.node.replace('\n', '').trim();
  const lowestNode = '10.0.0';
  if (semver.lt(nodeVersion, lowestNode)) {
    throw new Error(chalk.bold.red(`node版本必须是${lowestNode}以上`));
  }
};
module.exports = checkNodeAndNpmVersion;
