const createSignature = require('./createSignature');

module.exports = env => {
  const key = env.getEnv('NDP_KEY');
  const gwClientId = env.getEnv('NDP_SID');
  const gwClientUser = env.getEnv('NDP_USR');
  const { sig, time } = createSignature(key);
  return {
    gwClientId,
    gwClientUser,
    gwTimestamp: time,
    gwSignature: sig,
  };
};
